INSERT INTO videos (title, status, video_type, scheduled_at, r2_url, r2_transcript_url, video_filename, content_type, file_size_bytes, transcript_id, planly_media_id, social_copy_hook, social_copy_body, social_copy_cta, hashtags) VALUES ('How do I Progress?', 'Scheduled', 'Other', '7/24/2025 15:00', 'https://videos.gr8r.com/How-do-I-Progress.mov', 'https://videos.gr8r.com/transcripts/How_do_I_Progress.txt', 'How-do-I-Progress.mov', 'video/quicktime', 659371950.0, '6FkmbRGed3iXwOCd', NULL, 'Ever felt trapped by the chase for perfection?', 'This video dives deep into the allure of perfection and its impact on our lives. Our journey is about recognizing progress, not achieving faultlessness. It''s about embracing the present and valuing self-care over relentless pursuit. Let''s learn to see our efforts as acts of love rather than steps to an unreachable goal.', 'How do you measure progress in your life? Share your thoughts!', '#ProgressNotPerfection #SelfReflection #InnerGrowth #EmbraceTheJourney #GrowthMindset #GR8R');
INSERT INTO videos (title, status, video_type, scheduled_at, r2_url, r2_transcript_url, video_filename, content_type, file_size_bytes, transcript_id, planly_media_id, social_copy_hook, social_copy_body, social_copy_cta, hashtags) VALUES ('Pivot Year Day 205', 'Scheduled', 'Pivot Year', '7/24/2025 08:50', 'https://videos.gr8r.com/Pivot-Year-Day-205.mov', 'https://videos.gr8r.com/transcripts/Pivot_Year_Day_205.txt', 'Pivot-Year-Day-205.mov', 'video/quicktime', 109105284.0, 'MxaNeHgdn9TQzWaU', NULL, 'Are you letting your emotions dictate your reality?', 'Brianna Wiest reminds us that emotions aren''t just feelings; they''re calls to action. Respond wisely before they spiral out of control. Fear and shame diminish when we truly listen. Your emotions are like kids—get curious, understand them, and watch your world transform.', 'How do you respond to your emotions before they get loud? Share your thoughts!', '#EmotionalIntelligence #MindfulAction #SoulClimate #GR8R #ThePivotYear #briannawiest');
