INSERT INTO videos (title, status, video_type, scheduled_at, r2_url, r2_transcript_url, video_filename, content_type, file_size_bytes, transcript_id, planly_media_id, social_copy_hook, social_copy_body, social_copy_cta, hashtags) VALUES ('Needing is not loving', 'Scheduled', 'Other', '7/7/2025 15:01', 'https://videos.gr8r.com/needing-is-not-loving.mov', NULL, 'Needing is not loving.mov', 'video/quicktime', 85062794.0, 'SAdUuhx5pSxlRtcT', NULL, 'Ever felt like love and need are the same? Think again.', 'Needing someone can mask true love. Love is rooted in self-acceptance and abundance, not dependency. When you love from a place of strength, you give freely, not out of obligation. How can you cultivate self-love today?', 'What does love mean to you in your relationships?', '#SelfLoveMatters #HealthyBoundaries #LoveWithoutNeed #GR8R #ThePivotYear #briannawiest');
