INSERT INTO videos (title, status, video_type, scheduled_at, r2_url, r2_transcript_url, video_filename, content_type, file_size_bytes, transcript_id, planly_media_id, social_copy_hook, social_copy_body, social_copy_cta, hashtags) VALUES ('Pivot Year Day 186', 'Scheduled', 'Pivot Year', '7/5/2025 08:00', 'https://videos.gr8r.com/pivot-year-day-186.mov', NULL, 'Pivot Year Day 186.mov', 'video/quicktime', 91705174.0, 'uCYLrOCwwkOo12Cq', NULL, 'Are you trapped in a cycle of comfort?', 'Brianna Wiest invites you to reflect on how our brains often resist change, leading us to cling to familiar discomfort. True growth comes from recognizing these impulses and daring to choose differently, even when it feels uncomfortable.', 'What’s one small step you can take today to embrace the unknown?', '#BreakFree #EmbraceGrowth #MindfulLiving #GR8R #ThePivotYear #briannawiest');
