INSERT INTO videos (title, status, video_type, scheduled_at, r2_url, r2_transcript_url, video_filename, content_type, file_size_bytes, transcript_id, planly_media_id, social_copy_hook, social_copy_body, social_copy_cta, hashtags) VALUES ('Narcissism and self-love', 'Scheduled', 'Other', '7/3/2025 15:01', 'https://videos.gr8r.com/narcissism-and-self-love.mov', NULL, 'Narcissism and self-love.mov', 'video/quicktime', 95562031.0, 'qhiQj4sXdzizHmKp', NULL, 'Ever wondered if you might be a narcissist?', 'The concern often comes from a lack of self-love, not an excess. True narcissism stems from internal voids, leading to the need for external validation. Prioritize self-love to avoid the pitfalls of narcissistic tendencies.', 'What steps do you take to nurture your self-love?', '#SelfLoveJourney #NarcissismAwareness #MentalHealthMatters #GR8R #ThePivotYear #briannawiest');
