INSERT INTO videos (title, status, video_type, scheduled_at, r2_url, r2_transcript_url, video_filename, content_type, file_size_bytes, transcript_id, planly_media_id, social_copy_hook, social_copy_body, social_copy_cta, hashtags) VALUES ('Pivot Year Day 130', 'Scheduled', 'Pivot Year', '5/10/2025 08:00', 'https://videos.gr8r.com/pivot-year-day-130.mov', NULL, 'Pivot Year Day 130.mp4', 'video/mp4', 94882838.0, 'HTIPgs5HmWDvlK1d', NULL, 'Ever felt the weight of your emotions?', 'In moments of frustration, we have a choice: bottle it up or examine it. Brianna Wiest reminds us that true healing comes from unpacking our burdens, questioning what holds us back, and transforming discomfort into growth. Let’s embrace the beauty of change together!', 'What emotional weight are you ready to unpack today?', '#SelfDiscovery #HealingJourney #EmbraceChange #GR8R #ThePivotYear #briannawiest');
INSERT INTO videos (title, status, video_type, scheduled_at, r2_url, r2_transcript_url, video_filename, content_type, file_size_bytes, transcript_id, planly_media_id, social_copy_hook, social_copy_body, social_copy_cta, hashtags) VALUES ('The-right-partner1', 'Scheduled', 'Other', '5/9/2025 15:00', 'https://videos.gr8r.com/the-right-partner1.mov', NULL, 'The-right-partner1.mp4', 'video/mp4', 87693638.0, 'VcvNnqU08vOO6NDh', NULL, 'What if the real question isn''t about finding the right person?', 'It''s about becoming the right person. Healthy relationships stem from self-love and strong boundaries. When you prioritize your own well-being, you naturally attract what you deserve. It''s time to shift the focus inward.', 'How are you working on yourself today?', '#SelfLove #HealthyBoundaries #RelationshipGoals #GR8R #ThePivotYear #briannawiest');
INSERT INTO videos (title, status, video_type, scheduled_at, r2_url, r2_transcript_url, video_filename, content_type, file_size_bytes, transcript_id, planly_media_id, social_copy_hook, social_copy_body, social_copy_cta, hashtags) VALUES ('Pivot Year Day 129', 'Scheduled', 'Pivot Year', '5/9/2025 08:00', 'https://videos.gr8r.com/pivot-year-day-129.mov', NULL, NULL, NULL, NULL, 'dNbp7x5In0IrNkOk', NULL, 'Are you feeling overwhelmed by life’s expectations?', 'Growing up means realizing that not everything is meant for you. It’s essential to focus on becoming the best version of yourself rather than chasing every trend or opportunity. In a fast-paced world, we often forget to live in the moment. Embrace your unique path and remember, you’re meant for greater things!', 'What steps are you taking to live more authentically?', '#FearOfMissingOut #LiveInTheMoment #PersonalGrowth #GR8R #ThePivotYear #briannawiest');
INSERT INTO videos (title, status, video_type, scheduled_at, r2_url, r2_transcript_url, video_filename, content_type, file_size_bytes, transcript_id, planly_media_id, social_copy_hook, social_copy_body, social_copy_cta, hashtags) VALUES ('Pivot Year Day 128', 'Scheduled', 'Pivot Year', '5/8/2025 12:03', 'https://videos.gr8r.com/pivot-year-day-128.mov', NULL, NULL, NULL, NULL, 'Keye5kWabq8mk4I8', NULL, 'What if one tiny change could transform your entire life?', 'Brianna Wiest reminds us that the fabric of our reality can shift with one small thread. By introducing tiny habits, we can create waves of transformation in our lives. It’s not about grand gestures but celebrating small victories. Embrace the little things and watch your life unfold.', 'What tiny change will you make today? Share below!', '#TinyHabits #TransformYourLife #SelfLove #GR8R #ThePivotYear #briannawiest');
INSERT INTO videos (title, status, video_type, scheduled_at, r2_url, r2_transcript_url, video_filename, content_type, file_size_bytes, transcript_id, planly_media_id, social_copy_hook, social_copy_body, social_copy_cta, hashtags) VALUES ('Pivot Year Day 127', 'Scheduled', 'Pivot Year', '5/7/2025 12:00', 'https://videos.gr8r.com/pivot-year-day-127.mov', NULL, NULL, NULL, NULL, 'FqqFlXSdkNIFYKCI', NULL, 'What if happiness is about appreciating what you already have?', 'Brianna Wiest reminds us that true contentment lies in recognizing that we already possess everything we need. It’s a profound realization that challenges our conventional desires. Embracing our current circumstances without judgment allows us to find peace amidst chaos.', 'How do you practice gratitude in your daily life?', '#MindfulnessMatters #EmbraceWhatIs #HappinessJourney #GR8R #ThePivotYear #briannawiest');
INSERT INTO videos (title, status, video_type, scheduled_at, r2_url, r2_transcript_url, video_filename, content_type, file_size_bytes, transcript_id, planly_media_id, social_copy_hook, social_copy_body, social_copy_cta, hashtags) VALUES ('Pivot Year Day 126', 'Scheduled', 'Pivot Year', '5/6/2025 08:00', 'https://videos.gr8r.com/pivot-year-day-126.mov', NULL, NULL, NULL, NULL, NULL, NULL, 'What if understanding others is simpler than we think?', 'Brianna highlights how people''s actions reflect their true feelings. When they say ''no,'' it’s not just a rejection; it’s a clear boundary. We often complicate relationships with our narratives, but their honesty is refreshing. Embracing this clarity can transform how we connect with ourselves and others.', 'How do you interpret the actions of those around you?', '#EmotionalIntelligence #AuthenticConnections #SelfAwareness #GR8R #ThePivotYear #briannawiest');
INSERT INTO videos (title, status, video_type, scheduled_at, r2_url, r2_transcript_url, video_filename, content_type, file_size_bytes, transcript_id, planly_media_id, social_copy_hook, social_copy_body, social_copy_cta, hashtags) VALUES ('Pivot Year Day 125', 'Scheduled', 'Pivot Year', '5/5/2025 08:00', 'https://videos.gr8r.com/pivot-year-day-125.mov', NULL, NULL, NULL, NULL, NULL, NULL, 'Are you ready to embrace the new you?', 'As we step into this pivotal year, remember that not every part of you needs to change. Some aspects simply need love and acceptance. When you start loving yourself, it breaks free everything else holding you back from greatness.', 'What part of you are you ready to embrace this year?', '#SelfLoveJourney #EmbraceChange #LoveYourself #GR8R #ThePivotYear #briannawiest');
